SELECT grado.nombre, materia.nombre, competencia.competencia, competencia.orden, competencia.tipo_competencia FROM competencia INNER JOIN materia ON materia.idmateria = competencia.materia AND competencia.estado = 'activo' INNER JOIN grado ON materia.grado = grado.idgrado ORDER BY grado.idgrado, materia.nombre

mysql -u root -p auxiliar -e "SELECT grado.nombre, materia.nombre, competencia.competencia, competencia.orden, competencia.tipo_competencia INTO OUTFILE '/competencias-acts.csv' FIELDS TERMINATED BY ';' OPTIONALLY ENCLOSED BY '\"' LINES TERMINATED BY '\n' FROM competencia INNER JOIN materia ON materia.idmateria = competencia.materia AND competencia.estado = 'activo' INNER JOIN grado ON materia.grado = grado.idgrado ORDER BY grado.idgrado, materia.nombre ;"


